import React from "react";
import "./UserDashboard.css";
import { Redirect } from "react-router-dom";
import { useSelector } from "react-redux";
import FirstLoginModal from "./components/FirstLoginModal";

const UserDashboard = () => {
  const [modalShow, setModalShow] = React.useState(false);
  const isUnlocked = useSelector((state) => state.isloggedWithMetamask);
  if (isUnlocked) {
    return (
      <div>
        {/* TODO: Implement conditional if first login */}
        <FirstLoginModal show={modalShow} onHide={() => setModalShow(false)}/>
      </div>
    );
  }
  // Todo: implement modal saying you have to login. reload page live, no hard refresh
  return <Redirect to="/" />;
};

export default UserDashboard;
